// src/visualizations/UrbanNetworkGraph.js
import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';

const UrbanNetworkGraph = () => {
  const svgRef = useRef(null);
  const [period, setPeriod] = useState('current'); // 'pre-covid', 'covid', 'recovery', 'current'
  const [correlationData, setCorrelationData] = useState(null);
  const [loading, setLoading] = useState(true);
  
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        // Fetch correlation data from your API
        const response = await fetch(`/api/urban-correlations?period=${period}`);
        const data = await response.json();
        setCorrelationData(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching correlation data:', error);
        setLoading(false);
      }
    };
    
    fetchData();
  }, [period]);
  
  useEffect(() => {
    if (!correlationData || loading) return;
    
    // Clear previous visualization
    d3.select(svgRef.current).selectAll('*').remove();
    
    // Setup SVG
    const width = svgRef.current.clientWidth;
    const height = svgRef.current.clientHeight;
    const svg = d3.select(svgRef.current)
      .attr('width', width)
      .attr('height', height);
    
    // Process data for visualization
    const { nodes, links } = correlationData;
    
    // Color scale for node categories
    const colorScale = d3.scaleOrdinal()
      .domain(['borough', 'transportation', 'complaint', 'weather'])
      .range(['#6baed6', '#fd8d3c', '#74c476', '#9e9ac8']);
    
    // Size scale for node importance
    const sizeScale = d3.scaleLinear()
      .domain([0, d3.max(nodes, d => d.weight || 1)])
      .range([5, 15]);
    
    // Create a force simulation
    const simulation = d3.forceSimulation(nodes)
      .force('link', d3.forceLink(links).id(d => d.id).distance(d => 100 / (d.value + 0.1)))
      .force('charge', d3.forceManyBody().strength(-100))
      .force('center', d3.forceCenter(width / 2, height / 2))
      .force('collision', d3.forceCollide().radius(d => sizeScale(d.weight || 1) + 10));
    
    // Create a group for all elements
    const g = svg.append('g');
    
    // Add zoom behavior
    svg.call(d3.zoom()
      .extent([[0, 0], [width, height]])
      .scaleExtent([0.1, 8])
      .on('zoom', (event) => {
        g.attr('transform', event.transform);
      }));
    
    // Create links
    const link = g.append('g')
      .selectAll('line')
      .data(links)
      .enter()
      .append('line')
      .attr('stroke-width', d => Math.abs(d.value) * 2)
      .attr('stroke', d => d.value >= 0 ? '#999' : '#f44336')
      .attr('stroke-dasharray', d => d.value >= 0 ? '0' : '3,3')
      .attr('opacity', d => Math.abs(d.value) * 0.8 + 0.2);
    
    // Create node groups
    const node = g.append('g')
      .selectAll('.node')
      .data(nodes)
      .enter()
      .append('g')
      .attr('class', 'node')
      .call(d3.drag()
        .on('start', dragstarted)
        .on('drag', dragged)
        .on('end', dragended));
    
    // Add circles to node groups
    node.append('circle')
      .attr('r', d => sizeScale(d.weight || 1))
      .attr('fill', d => colorScale(d.category))
      .attr('stroke', '#fff')
      .attr('stroke-width', 1.5);
    
    // Add labels to node groups
    node.append('text')
      .attr('dx', d => sizeScale(d.weight || 1) + 5)
      .attr('dy', '.35em')
      .text(d => d.name)
      .style('font-size', '10px')
      .style('fill', '#333');
    
    // Add tooltips
    node.append('title')
      .text(d => `${d.name}\nType: ${d.category}\nWeight: ${d.weight || 1}`);
    
    // Add tooltips to links
    link.append('title')
      .text(d => `${d.source.name} → ${d.target.name}\nCorrelation: ${d.value.toFixed(2)}`);
    
    // Update positions on each tick
    simulation.on('tick', () => {
      link
        .attr('x1', d => d.source.x)
        .attr('y1', d => d.source.y)
        .attr('x2', d => d.target.x)
        .attr('y2', d => d.target.y);
      
      node
        .attr('transform', d => `translate(${d.x},${d.y})`);
    });
    
    // Drag functions
    function dragstarted(event, d) {
      if (!event.active) simulation.alphaTarget(0.3).restart();
      d.fx = d.x;
      d.fy = d.y;
    }
    
    function dragged(event, d) {
      d.fx = event.x;
      d.fy = event.y;
    }
    
    function dragended(event, d) {
      if (!event.active) simulation.alphaTarget(0);
      d.fx = null;
      d.fy = null;
    }
    
    // Legend
    const legend = svg.append('g')
      .attr('class', 'legend')
      .attr('transform', `translate(20, 20)`);
    
    const categories = ['borough', 'transportation', 'complaint', 'weather'];
    const legendItems = legend.selectAll('.legend-item')
      .data(categories)
      .enter()
      .append('g')
      .attr('class', 'legend-item')
      .attr('transform', (d, i) => `translate(0, ${i * 20})`);
    
    legendItems.append('circle')
      .attr('r', 6)
      .attr('fill', d => colorScale(d));
    
    legendItems.append('text')
      .attr('x', 15)
      .attr('y', 4)
      .text(d => d.charAt(0).toUpperCase() + d.slice(1))
      .style('font-size', '12px');
    
    // Correlation strength legend
    const corrLegend = svg.append('g')
      .attr('class', 'corr-legend')
      .attr('transform', `translate(20, ${height - 80})`);
    
    corrLegend.append('text')
      .attr('y', -10)
      .text('Correlation Strength:')
      .style('font-size', '12px');
    
    // Positive correlation
    corrLegend.append('line')
      .attr('x1', 0)
      .attr('y1', 10)
      .attr('x2', 30)
      .attr('y2', 10)
      .attr('stroke', '#999')
      .attr('stroke-width', 3);
    
    corrLegend.append('text')
      .attr('x', 35)
      .attr('y', 14)
      .text('Positive')
      .style('font-size', '10px');
    
    // Negative correlation
    corrLegend.append('line')
      .attr('x1', 0)
      .attr('y1', 30)
      .attr('x2', 30)
      .attr('y2', 30)
      .attr('stroke', '#f44336')
      .attr('stroke-width', 3)
      .attr('stroke-dasharray', '3,3');
    
    corrLegend.append('text')
      .attr('x', 35)
      .attr('y', 34)
      .text('Negative')
      .style('font-size', '10px');
    
  }, [correlationData, loading]);
  
  return (
    <div className="urban-network-container">
      <div className="controls">
        <h3>Time Period</h3>
        <div className="button-group">
          <button 
            className={period === 'pre-covid' ? 'active' : ''} 
            onClick={() => setPeriod('pre-covid')}
          >
            Pre-COVID
          </button>
          <button 
            className={period === 'covid' ? 'active' : ''} 
            onClick={() => setPeriod('covid')}
          >
            During COVID
          </button>
          <button 
            className={period === 'recovery' ? 'active' : ''} 
            onClick={() => setPeriod('recovery')}
          >
            Recovery
          </button>
          <button 
            className={period === 'current' ? 'active' : ''} 
            onClick={() => setPeriod('current')}
          >
            Current
          </button>
        </div>
      </div>
      
      {loading ? (
        <div className="loading">Loading correlation data...</div>
      ) : (
        <svg 
          ref={svgRef} 
          width="100%" 
          height="600"
          style={{ border: '1px solid #ddd', borderRadius: '4px' }}
        ></svg>
      )}
      
      <div className="description">
        <h3>Understanding the Urban Network</h3>
        <p>
          This visualization shows how different urban systems correlate with each other. 
          Nodes represent boroughs, transportation modes, complaint types, and weather factors.
          Links represent relationships between them - solid lines show positive correlations,
          while dashed red lines show negative correlations. Stronger correlations have thicker lines.
        </p>
        <p>
          Try dragging nodes to explore the network and use the mouse wheel to zoom in and out.
          Toggle between time periods to see how relationships changed before, during, and after COVID-19.
        </p>
      </div>
    </div>
  );
};

export default UrbanNetworkGraph;
